var nopassIE = {
  init: function () {
    this.creatStyle()
    this.creatLoadDiv()
  },
  creatLoadDiv: function () {
    var newElement = document.createElement('div')
    newElement.className = 'killie-box'
    newElement.id = 'killie'
    newElement.innerHTML =
    `<div class="killie">
      <div class="killie-hd">
        <h3>您的浏览器版本过低</h3>
        <span>为了保证能获取更好用户体验，建议升级浏览器</span>
        <span>您可以升级到IE9以上</span>
      </div>
      <div class="killie-bd">
        <span>
          <a target="_blank" href="http://windows.microsoft.com/zh-cn/internet-explorer/download-ie">
            <img src="data:image/png;base64,"/>
          </a>
        </span>
      </div>
      <div class="killie-hd">
        <span>或者推荐使用以下浏览器，记得使用极速模式</span>
      </div>
      <div class="killie-bd">
        <span>
          <a target="_blank" href="http://www.google.cn/intl/zh-CN/chrome/browser/desktop/index.html">
            <img src="data:image/png;base64,"/></a></span><span><a target="_blank" href="http://chrome.360.cn/">
            <img src="data:image/png;base64,"/>
          </a>
        </span>
      </div>
    </div>
    <div class="killie-bg"></div>`
    document.body.appendChild(newElement)
  },
  creatStyle: function () {
    var nod = document.createElement('style')
    var str = '.killie{position:fixed;width:800px;height:480px;left:0;right:0;top:0;bottom:0;margin:auto;overflow:hidden;background:#FFF;border:1px solid #DDD;z-index:1000;_position:absolute;_top:expression(eval(document.compatMode && document.compatMode=="CSS1Compat")?documentElement.scrollTop+(documentElement.clientHeight-this.clientHeight):document.body.scrollTop +(document.body.clientHeight-this.clientHeight));_bottom:auto}.killie-hd{font-size:20px;text-align:center}.killie-hd h3{margin-top:28px}.killie-hd span{font-size:18px;margin-top:20px;display:block}.killie-bd span{display:block;margin:10px 0}.killie-bd{text-align:center;overflow:hidden}.killie-bg{position:fixed;top:0px;left:0px;width:100%;height:200%;background:#000;opacity:0.4;filter:alpha(opacity=40);z-index:999}'
    nod.type = 'text/css'
    nod.styleSheet ? nod.styleSheet.cssText = str : nod.innerHTML = str
    document.getElementsByTagName('head')[0].appendChild(nod)
  }
}
window.onload = function () {
  nopassIE.init()
}
